(518, '_transient_wpsso_8f7d54a1c8af964fe8d2ff83c7bd4a25', 'a:16:{s:4:"name";s:37:"WordPress Social Sharing Optimization";s:4:"tags";a:44:{i:0;s:7:"nextgen";i:1;s:8:"featured";i:2;s:8:"attached";i:3;s:10:"open graph";i:4;s:4:"meta";i:5;s:8:"facebook";i:6;s:6:"google";i:7;s:11:"google plus";i:8;s:2:"g+";i:9;s:7:"twitter";i:10;s:8:"linkedin";i:11;s:6:"social";i:12;s:3:"seo";i:13;s:9:"pinterest";i:14;s:9:"rich pins";i:15;s:6:"tumblr";i:16;s:11:"stumbleupon";i:17;s:6:"widget";i:18;s:8:"language";i:19;s:12:"multilingual";i:20;s:12:"object cache";i:21;s:15:"transient cache";i:22;s:8:"wp_cache";i:23;s:8:"nggalbum";i:24;s:9:"nggallery";i:25;s:9:"singlepic";i:26;s:12:"imagebrowser";i:27;s:15:"nextgen gallery";i:28;s:7:"gallery";i:29;s:13:"twitter cards";i:30;s:10:"photo card";i:31;s:12:"gallery card";i:32;s:11:"player card";i:33;s:24:"large image summary card";i:34;s:12:"summary card";i:35;s:11:"woocommerce";i:36;s:11:"marketpress";i:37;s:10:"e-commerce";i:38;s:9:"multisite";i:39;s:8:"hashtags";i:40;s:7:"bbpress";i:41;s:10:"buddypress";i:42;s:7:"jetpack";i:43;s:6:"photon";}s:17:"requires_at_least";s:3:"3.0";s:12:"tested_up_to";s:3:"3.8";s:10:"stable_tag";s:6:"1.22.1";s:12:"contributors";a:1:{i:0;s:8:"jsmoriss";}s:11:"donate_link";s:44:"http://surniaulula.com/extend/plugins/wpsso/";s:7:"license";s:5:"GPLv3";s:11:"license_uri";s:63:"http://surniaulula.com/wp-content/plugins/wpsso/license/gpl.txt";s:17:"short_description";s:127:"Improve the appearance and ranking of WordPress Posts, Pages, and eCommerce Products in Google Search and Social Website shares";s:11:"screenshots";a:18:{i:0;s:36:"Screenshot 1 : General Settings Page";i:1;s:37:"Screenshot 2 : Advanced Settings Page";i:2;s:45:"Screenshot 3 : An Example Facebook Link Share";i:3;s:46:"Screenshot 4 : An Example Facebook Video Share";i:4;s:44:"Screenshot 5 : An Example Google+ Link Share";i:5;s:45:"Screenshot 6 : An Example Google+ Video Share";i:6;s:74:"Screenshot 7 : An Example Google Search Result showing Author Profile Info";i:7;s:40:"Screenshot 8 : An Example LinkedIn Share";i:8;s:45:"Screenshot 9 : An Example Pinterest Image Pin";i:9;s:44:"Screenshot 10 : An Example StumbleUpon Share";i:10;s:46:"Screenshot 11 : An Example Tumblr ''Link'' Share";i:11;s:47:"Screenshot 12 : An Example Tumblr ''Photo'' Share";i:12;s:47:"Screenshot 13 : An Example Tumblr ''Video'' Share";i:13;s:49:"Screenshot 14 : An Example Twitter ''Summary'' Card";i:14;s:61:"Screenshot 15 : An Example Twitter ''Large Image Summary'' Card";i:15;s:47:"Screenshot 16 : An Example Twitter ''Photo'' Card";i:16;s:49:"Screenshot 17 : An Example Twitter ''Gallery'' Card";i:17;s:81:"Screenshot 18 : An Example Twitter ''Product'' Card from a WooCommerce Product Page";}s:10:"is_excerpt";b:0;s:12:"is_truncated";b:0;s:8:"sections";a:5:{s:11:"description";s:6481:"<p><strong>WordPress Social Sharing Optimization (WPSSO) is a fork of the excellent <a href="http://wordpress.org/plugins/nextgen-facebook/">NGFB Open Graph+</a> plugin - it has all the same great features, but strives to be a little lighter and smaller by removing the social buttons and their related features (shortcodes, widgets, stylesheets, javascript caching, url shortening, and url rewriting)</strong>.</p>\n\n<h4>Summary of Features</h4>\n\n<ul>\n<li>Facebook / Google+ / LinkedIn / etc. Open Graph, Pinterest Rich Pin, and Twitter Card meta tags.</li>\n<li>Configurable image sizes and descriptions depending on context.</li>\n<li>Configurable contact fields for author meta tags and user profile pages.</li>\n<li><strong>Integrates with 3rd party plugins and services for additional image, video, product, and content information</strong>:\n\n<ul>\n<li>NextGEN Gallery</li>\n<li>JetPack Photon</li>\n<li>WordPress SEO by Yoast</li>\n<li>All in One SEO Pack</li>\n<li>WooCommerce</li>\n<li>MarketPress</li>\n<li>WP e-Commerce</li>\n<li>bbPress</li>\n<li>BuddyPress</li>\n<li>Youtube / Vimeo / Wistia APIs</li>\n</ul></li>\n</ul>\n\n<h4>Complete Meta Tags</h4>\n\n<p>WPSSO adds Facebook / <a href="http://ogp.me/">Open Graph</a>, <a href="http://developers.pinterest.com/rich_pins/">Pinterest Rich Pins</a>, <a href="https://dev.twitter.com/docs/cards">Twitter Cards</a>, and (optionally) <a href="http://en.wikipedia.org/wiki/Search_engine_optimization">Search Engine Optimization</a> meta tags to the head section of webpages. These meta tags are used by Google Search and most Social Websites to describe and display your content correctly (title, description, keyword tags, images, videos, product, author profile / authorship, publisher, etc.). <a href="/extend/plugins/wpsso/screenshots/">See a few examples from Google Search / Google+, Facebook, Twitter, Pinterest, StumbleUpon, Tumblr, and others</a>.</p>\n\n<p>WPSSO (Pro version) provides the <a href="https://dev.twitter.com/docs/cards/types/summary-card">Summary</a>, <a href="https://dev.twitter.com/docs/cards/large-image-summary-card">Large Image Summary</a>, <a href="https://dev.twitter.com/docs/cards/types/photo-card">Photo</a>, <a href="https://dev.twitter.com/docs/cards/types/gallery-card">Gallery</a>, <a href="https://dev.twitter.com/docs/cards/types/player-card">Player</a> and <a href="https://dev.twitter.com/docs/cards/types/product-card">Product</a> Twitter Cards, including configurable image sizes for each card type.</p>\n\n<h4>Best Performance</h4>\n\n<p><strong>WPSSO is fast and tuned for performance</strong>, and unlike most plugins, makes full use of all available caching techniques (persistent / non-persistent object and disk caching).</p>\n\n<p><strong>WPSSO only loads the library files and object classes it needs</strong>, keeping it small, fast, and yet still able to support a wide range of 3rd party integration features.</p>\n\n<h4>3rd Party Integration</h4>\n\n<p>Aside from the additional support for Twitter Cards, the main difference between the GPL and Pro versions is the integration of 3rd party plugins and services.</p>\n\n<p><strong>Images and Videos</strong></p>\n\n<p>WPSSO detects and uses all images - associated or included in your Post or Page content - including WordPress Media Library image galleries and embedded videos from YouTube, Vimeo, and Wistia (including their preview images). WordPress Media Library images (and NextGEN Gallery in the Pro version) are resized and provided according to their intended audience (Facebook, Twitter, Pinterest, etc).</p>\n\n<p>WPSSO (Pro version) also includes support for <a href="http://jetpack.me/support/photon/">JetPack Photon</a> and <a href="http://wordpress.org/plugins/nextgen-gallery/">NextGEN Gallery v1 and v2</a> albums, galleries and images (shortcodes, image tags, album / gallery preview images, etc.).</p>\n\n<p><strong>Enhanced SEO</strong></p>\n\n<p>WPSSO (Pro version) integrates with <a href="http://wordpress.org/plugins/wordpress-seo/">WordPress SEO by Yoast</a> and <a href="http://wordpress.org/plugins/all-in-one-seo-pack/">All in One SEO Pack</a>, making sure your custom SEO settings are reflected in the Open Graph, Rich Pin, and Twitter Card meta tags.</p>\n\n<p><strong>eCommerce Products</strong></p>\n\n<p>WPSSO (Pro version) also supports <a href="http://wordpress.org/plugins/woocommerce/">WooCommerce v1 and v2</a>, <a href="http://wordpress.org/plugins/wordpress-ecommerce/">MarketPress WordPress eCommerce</a> and <a href="http://wordpress.org/plugins/wp-e-commerce/">WP e-Commerce</a> product pages, creating appropriate meta tags for <a href="https://developers.facebook.com/docs/payments/product/">Facebook Products</a>, <a href="https://dev.twitter.com/docs/cards/types/product-card">Twitter Product Cards</a> and <a href="http://developers.pinterest.com/rich_pins/">Pinterest Rich Pins</a>, including variations and additional / custom images.</p>\n\n<p><strong>Forums and Social</strong></p>\n\n<p>WPSSO (Pro version) supports <a href="http://wordpress.org/plugins/bbpress/">bbPress</a> and <a href="http://wordpress.org/plugins/buddypress/">BuddyPress</a> (see the <a href="http://surniaulula.com/codex/plugins/wpsso/notes/buddypress-integration/">BuddyPress Integration Notes</a>), making sure your meta tags reflect the page content, including appropriate titles, descriptions, images, etc.</p>\n\n<h4>Custom Contacts</h4>\n\n<p>WPSSO (Pro version) allows you to customize the field names, label, and show / remove the following Contact Methods from the user profile page:</p>\n\n<ul>\n<li>AIM</li>\n<li>Facebook </li>\n<li>Google+ </li>\n<li>Jabber / Google Talk</li>\n<li>LinkedIn </li>\n<li>Pinterest </li>\n<li>Skype </li>\n<li>Tumblr </li>\n<li>Twitter </li>\n<li>Yahoo IM</li>\n<li>YouTube</li>\n</ul>\n\n<h4>Clean Uninstall</h4>\n\n<p><strong>Try the WPSSO plugin with complete confidence</strong> - when uninstalled, WPSSO removes <em>all</em> traces of itself from the database (options, site options, user and post meta, transients, etc.).</p>\n\n<h4>Great Support</h4>\n\n<p><strong>WPSSO support and development is on-going</strong>. You can review the <a href="http://faq.wpsso.surniaulula.com/">FAQ</a> and <a href="http://notes.wpsso.surniaulula.com/">Notes</a> pages for additional setup information. If you have any suggestions or comments, post them to the <a href="http://wordpress.org/support/plugin/wpsso">WordPress support forum</a> or the <a href="http://support.wpsso.surniaulula.com/">Pro version support website</a>.</p>";s:12:"installation";s:418:"<h4>How-To</h4>\n\n<ul>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/how-to/install-the-plugin/">Install the Plugin</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/how-to/uninstall-the-plugin/">Uninstall the Plugin</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/how-to/migrate-from-ngfb-open-graph-to-wpsso/">Migrate from NGFB Open Graph+ to WPSSO</a></li>\n</ul>";s:26:"frequently_asked_questions";s:3689:"<h4>Frequently Asked Questions</h4>\n\n<ul>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/can-i-use-the-pro-version-on-multiple-websites/">Can I use the Pro version on multiple websites?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/does-linkedin-read-the-open-graph-meta-tags/">Does LinkedIn read the Open Graph meta tags?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/doesnt-an-seo-plugin-cover-that/">Doesn‚Äôt an SEO plugin cover that?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/how-can-i-exclude-ignore-certain-parts-of-the-content-text/">How can I exclude / ignore certain parts of the content text?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/how-can-i-see-what-facebook-sees/">How can I see what Facebook sees?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/how-can-i-share-a-single-nextgen-gallery-image/">How can I share a single NextGEN Gallery image?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/how-do-i-attach-an-image-without-showing-it-on-the-webpage/">How do I attach an image without showing it on the webpage?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/how-do-i-install-the-wpsso-pro-version/">How do I install the WPSSO Pro version?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/how-does-wpsso-find-images/">How does WPSSO find images?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/w3c-says-there-is-no-attribute-property/">W3C says ‚Äúthere is no attribute ‚Äòproperty‚Äô‚Äù</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/what-about-google-search-and-google-plus/">What about Google Search and Google Plus?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/what-features-of-nextgen-gallery-are-supported/">What features of NextGEN Gallery are supported?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/what-is-the-difference-between-the-gpl-and-pro-versions/">What is the difference between the GPL and Pro versions?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/where-do-i-start/">Where do I start?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/why-arent-pins-from-my-website-posting-rich/">Why aren‚Äôt Pins from my website posting Rich?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/why-do-my-facebook-shares-have-small-images/">Why do my Facebook shares have small images?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/why-does-facebook-play-videos-instead-of-linking-them/">Why does Facebook play videos instead of linking them?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/why-does-google-structured-data-testing-tool-show-errors/">Why does Google Structured Data Testing Tool show errors?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/why-does-wpsso-ignore-some-img-html-tags/">Why does WPSSO ignore some &lt;img/&gt; HTML tags?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/why-doesnt-facebook-show-the-correct-image/">Why doesn‚Äôt Facebook show the correct image?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/why-dont-my-twitter-cards-show-on-twitter/">Why don‚Äôt my Twitter Cards show on Twitter?</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/faq/why-is-the-open-graph-title-the-same-for-every-webpage/">Why is the Open Graph title the same for every webpage?</a></li>\n</ul>";s:11:"screenshots";s:1073:"<ol>\n<li>Screenshot 1 : General Settings Page</li>\n<li>Screenshot 2 : Advanced Settings Page</li>\n<li>Screenshot 3 : An Example Facebook Link Share</li>\n<li>Screenshot 4 : An Example Facebook Video Share</li>\n<li>Screenshot 5 : An Example Google+ Link Share</li>\n<li>Screenshot 6 : An Example Google+ Video Share</li>\n<li>Screenshot 7 : An Example Google Search Result showing Author Profile Info</li>\n<li>Screenshot 8 : An Example LinkedIn Share</li>\n<li>Screenshot 9 : An Example Pinterest Image Pin</li>\n<li>Screenshot 10 : An Example StumbleUpon Share</li>\n<li>Screenshot 11 : An Example Tumblr ''Link'' Share</li>\n<li>Screenshot 12 : An Example Tumblr ''Photo'' Share</li>\n<li>Screenshot 13 : An Example Tumblr ''Video'' Share</li>\n<li>Screenshot 14 : An Example Twitter ''Summary'' Card</li>\n<li>Screenshot 15 : An Example Twitter ''Large Image Summary'' Card</li>\n<li>Screenshot 16 : An Example Twitter ''Photo'' Card</li>\n<li>Screenshot 17 : An Example Twitter ''Gallery'' Card</li>\n<li>Screenshot 18 : An Example Twitter ''Product'' Card from a WooCommerce Product Page</li>\n</ol>";s:9:"changelog";s:3946:"<h4>Version 1.23dev1</h4>\n\n<ul>\n<li>Fixed Twitter Card description in the admin interface (Pro version).</li>\n<li>Fixed WooCommerce product detection for meta tags in the admin interface (Pro version).</li>\n<li>Fixed the detection of an empty array (including its values) when image is rejected.</li>\n<li>Added Image Dimension options for Twitter Card images (Pro version).</li>\n</ul>\n\n<h4>Version 1.22.1</h4>\n\n<ul>\n<li>A minor update to fix a variable name used when upgrading the plugin options.</li>\n</ul>\n\n<h4>Version 1.22.0</h4>\n\n<p>The Website Topic list in the SSO General settings has been expanded, and the new topics list is now located in a <code>share/topics.txt</code> text file for easier maintenance.</p>\n\n<ul>\n<li>Added the WPSSO_META_NAME_ALT, WPSSO_OPTIONS_NAME_ALT, WPSSO_SITE_OPTIONS_NAME_ALT constants to allow for settings migration.</li>\n<li>Added an WPSSO_TOPICS_LIST constant, with a default value of WPSSO_PLUGINDIR.''share/topics.txt''.</li>\n<li>Created a new <code>share/topics.txt</code> text file by merging the Wikipedia and StumbleUpon website lists.</li>\n<li>Expanded the "Website Topic" selection using the new <code>share/topics.txt</code> list.</li>\n<li>Moved the <code>sucom_get_lang()</code> function to <code>SucomUtil::get_lang()</code>.</li>\n<li>Moved the default options variable definition from lib/options.php to lib/config.php.</li>\n<li>Added an <code>sanitize_option_value()</code> method in the WpssoUtil class.</li>\n<li>Added an ''wpsso_option_type'' filter to optimize sanitation of option values.</li>\n<li>Small improvements to the update check code.</li>\n</ul>\n\n<h4>Version 1.21.0</h4>\n\n<p>Please note that support for <a href="http://wordpress.org/plugins/nextgen-gallery/">the NextGEN Gallery plugin</a> has been moved to <a href="http://surniaulula.com/extend/plugins/wpsso/">the Pro version</a> in v1.21.0. This restores balance between the two distributions - <a href="http://wordpress.org/plugins/wpsso/">the WPSSO GPL version from WordPress.org</a> offers complete support for Open Graph+ meta tags and the WordPress Media Library (including its <code>gallery</code> shortcode), while the Pro version includes support for 3rd party plugins and services (like NextGEN Gallery, WordPress SEO, BuddyPress, bbPress, WooCommerce, Wistia videos, etc.). I remain committed to providing a complete and reliable set of features in the GPL version - all for free. There''s no need to contribute in any way, though a little <a href="http://wordpress.org/support/plugin/wpsso">help with forum questions</a>, for example, is always welcome. ;-)</p>\n\n<ul>\n<li><strong>Moved the NextGEN Gallery support to a Pro version addon</strong>.</li>\n<li>Added support for <strong>JetPack Photon</strong> to retrieve correct image sizes (Pro version).</li>\n<li>Added a ''wpsso_image_downsize'' hook (for the Jetpack Photon Pro addon).</li>\n<li>Added hooks to disable JetPack Open Graph and Twitter Card meta tags.</li>\n<li>Added a hook for the ''wpsso_sharing_url'' filter to support WordPress SEO''s "Force Transport" option (Pro version).</li>\n<li>Moved the URL rewriting and shortening methods into hooks for the ''wpsso_rewrite_url'' and ''wpsso_shorten_url'' filters (Pro version).</li>\n<li>Removed the "Disable Social Buttons" option from the SSO Custom Settings (Pro version).</li>\n<li>Added links to http URLs in the Open Graph+ Custom Settings ''Meta Tags Preview'' tab.</li>\n<li>Changed the default object expiration time from 1800 to 3600 seconds (1 hour).</li>\n<li>Removed the "Reset Settings on Activate" Advanced option.</li>\n<li>Renamed the WPSSO_RESET constant to WPSSO_RESET_ON_ACTIVATE.</li>\n<li>Added an ''wpsso_load_lib'' action to allow 3rd party plugins to provide library files.</li>\n<li>Moved several methods from lib/util.php to lib/com/util.php.</li>\n<li>Moved <code>show_sharing()</code> from lib/postmeta.php to lib/social.php.</li>\n<li>Renamed the ''WpssoPlugin'' main plugin class to ''Wpsso''.</li>\n</ul>";}s:17:"remaining_content";s:600:"<h3>Other / Additional Notes</h3>\n<ul>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/notes/constants/">Constants</a> A list of available PHP constants for the WPSSO plugin.</li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/notes/debugging-and-problem-solving/">Debugging and Problem Solving</a></li>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/notes/integration-notes/">Integration Notes</a>\n<ul>\n    <li><a href="http://surniaulula.com/codex/plugins/wpsso/notes/integration-notes/buddypress-integration/">BuddyPress Integration</a></li>\n</ul>\n</li>\n</ul>";s:14:"upgrade_notice";a:3:{s:6:"1.22.1";s:77:"A minor update to fix a variable name used when upgrading the plugin options.";s:6:"1.22.0";s:100:"Expanded the Website Topic list in the SSO General settings, new constants, small code improvements.";s:6:"1.21.0";s:147:"Moved NextGEN Gallery support to Pro version, added support for JetPack Photon, added support for WordPress SEO &quot;Force Transport&quot; option.";}}', 'no'),
